mapper.select:
select count(1) count
from scan_records srds
left join scan_area sa
on srds.area_no = sa.area_no
where 1 = 1

    and srds.depart_name like '%'  #{searchKey}  '%'


    and srds.record_status = #{statusSelect}


    and srds.depart_id = #{buMenId}


    and sa.id+100000 = #{quYuId}


    and  DATE_FORMAT(srds.create_time,'%Y-%m-%d') = #{dateSelect}


    and  srds.record_user_no = #{userInfo.userNo}

with myUpload as ( select srds.id, srds.area_no areaNo, sa.id + 100000 areaId, srds.area_name areaName, srds.depart_id departId, srds.depart_name departName, srds.manage_user manageUser, srds.manage_user_name manageUserName, srds.manage_phone managePhone, srds.`desc`, srds.item_ids itemIds, srds.images, srds.record_status recordStatus, case srds.record_status when 0 then '未通过' when 1 then '新增' when 2 then '已审核' when 3 then '已处理' when 4 then '已归档' end as 'recordStatusName', srds.record_user_no recordUserNo, srds.record_user_phone recordUserPhone, srds.record_location recordLocation, srds.record_location_name recordLocationName, srds.app_client_id appClientId, DATE_FORMAT(srds.update_time,'%Y-%m-%d %H:%i:%s') updateTime, DATE_FORMAT(srds.create_time,'%Y-%m-%d %H:%i:%s') createTime from scan_records srds left join scan_area sa on srds.area_no = sa.area_no ) select * from ( select (@r:=@r + 1) AS _row_number, id, areaNo, areaId, areaName, departId, departName, manageUser, manageUserName, managePhone, `desc`, itemIds, images, recordStatus, recordStatusName, recordUserNo, recordUserPhone, recordLocation, recordLocationName, appClientId, updateTime, createTime from myUpload,(select @r := 0) r where 1 = 1
and departName like '%' #{searchKey} '%'
and recordStatus = #{statusSelect}
and departId = #{buMenId}
and areaId = #{quYuId}
and DATE_FORMAT(createTime,'%Y-%m-%d') = #{dateSelect}
and recordUserNo = #{userInfo.userNo}
order by myUpload.createTime desc ) T where T._row_number > #{startPageNum} AND T._row_number <= #{endPageNum}
